&lt;pre&gt;&lt;code&gt;#在master节点之外的节点进行操作
kubeadm reset
systemctl stop kubelet
systemctl stop docker
rm -rf /var/lib/cni/
rm -rf /var/lib/kubelet/*
rm -rf /etc/cni/
ifconfig cni0 down
ifconfig flannel.1 down
ifconfig docker0 down
ip link delete cni0
ip link delete flannel.1
##重启kubelet
systemctl restart kubelet
##重启docker
systemctl restart docker
&lt;/code&gt;&lt;/pre&gt;
&lt;h5 id=&#34;2615-重启kubelet和docker&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#2615-重启kubelet和docker&#34;&gt;#&lt;/a&gt; 2.6.15 重启 kubelet 和 docker&lt;/h5&gt;
