um=&#34;15&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;16&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;mysql&gt; &lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;17&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;18&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;19&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;mysql&gt; set sql_log_bin=1;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;20&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;Query OK, 0 rows affected (0.00 sec)&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;21&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;22&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;mysql&gt; &lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;23&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;24&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;mysql&gt; start slave;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;25&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;Query OK, 0 rows affected (0.01 sec)&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;26&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;27&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;mysql&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;p&gt;&lt;strong&gt;在第三个节点 slave 上实现&lt;/strong&gt;&lt;/p&gt;
&lt;pre&gt;&lt;code&gt;vim /etc/my.cnf
[mysqld]
server-id=157
log-bin=/data/mysql/slave2-bin

# 还原数据库
vim /data/all.sql
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;img data-src=&#34;../image.assets/1669346692481.png&#34; alt=&#34;1669346692481&#34; /&gt;&lt;/p&gt;
&lt;pre&gt;&lt;code&gt;mysql&amp;gt; set sql_log_bin=0;
mysql&amp;gt; source /data/all.sql
mysql&amp;gt; set sql_log_bin=1;
mysql&amp;gt; start slave;
&lt;/code&gt;&lt;/pre&gt;
&lt;h3 id=&#34;半同步复制&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#半同步复制&#34;&gt;#&lt;/a&gt; 半同步复制&lt;/h3&gt;
&lt;p&gt;&lt;img data-src=&#34;../image.assets/1669367459560.png&#34; alt=&#34;1669367459560&#34; /&gt;&lt;/p&gt;
&lt;p&gt;范例：centos7.9 在 MySQL5.7.38 实现半同步复制&lt;/p&gt;
&lt;pre&gt;&lt;code&gt;# 主服务器配置，安装semisync_slave.so插件
mysql&amp;gt; INSTALL PLUGIN rpl_semi_sync_master SONAME &#39;semisync_master.so&#39;;
Query OK, 0 rows affected (0.05 sec)

mysql&amp;gt; 
# 修改配置文件
vim /etc/my.cnf
rpl_semi_sync_master_enabled=ON 
#修改此行,需要先安装semisync_master.so插件后,再重启,否则无法启动
rpl_semi_sync_master_timeout=3000 
#设置3s内无法同步，也将返回成功信息给客户端 

# 重启服务
systemctl restart mysql

#slave服务器配置
#安装semisync_slave.so插件
mysql&amp;gt; INSTALL PLUGIN rpl_semi_sync_master SONAME &#39;semisync_master.so&#39;;
Query OK, 0 rows affected (0.05 sec)

#注意:如果已经实现主从复制,需要stop slave;start slave;
mysql&amp;gt; stop slave;
Query OK, 0 rows affected (0.00 sec)

mysql&amp;gt; start slave
    -&amp;gt; ;
Query OK, 0 rows affected (0.00 sec)

mysql&amp;gt; 

# 重启mysql服务
systemctl restart mysql

# 修改配置文件
vim /etc/my.cnf
rpl_semi_sync_slave_enabled=ON

# 重启mysql服务
systemctl restart mysql
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;测试半同步&lt;/p&gt;
&lt;pre&gt;&lt;code&gt;# 停止从库slave1
[root@centos7 ~]# systemctl stop mysql
# 主库创建表
mysql&amp;gt; create database hellodb;
Query OK, 1 row affected (3.00 sec)

mysql&amp;gt; 
# 设置的是三秒内无法同步返回成功，所以看到是3S后返回成功

&lt;/code&gt;&lt;/pre&gt;
&lt;h3 id=&#34;实战案例利用mycat实现mysql的读写分离&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#实战案例利用mycat实现mysql的读写分离&#34;&gt;#&lt;/a&gt; 实战案例：利用 Mycat 实现 MySQL 的读写分离&lt;/h3&gt;
